String shortname = "";
Pattern p = Pattern.compile("([%#]\\d+) (.+[,;]) (.+)<.*");
Matcher m = p.matcher(OMIMdisease);
if (m.find()) { 
  shortname = m.group(3);
} else {
  p = Pattern.compile("([%#]\\d+) (.*)<.*");
  m = p.matcher(OMIMdisease);
  if (m.find()) shortname = m.group(2);
};

